import React from 'react';
import '../styles/FeaturesSection.css';

const features = [
  { title: 'Personalized Advice', description: 'Receive tailored legal advice based on your grievances.' },
  { title: 'IPC References', description: 'Get solutions grounded in Indian Penal Codes and court precedents.' },
  { title: 'Chat History', description: 'Save and refer back to your previous conversations.' }
];

const FeaturesSection = () => {
  return (
    <section className="features">
      <h2>Features</h2>
      <div className="features-container">
        {features.map((feature, index) => (
          <div key={index} className="feature-card">
            <h3>{feature.title}</h3>
            <p>{feature.description}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default FeaturesSection;
