import React, { useState } from "react";
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import {
  TextField,
  Button,
  Typography,
  InputAdornment,
  IconButton,
} from "@mui/material";
import { Visibility, VisibilityOff } from "@mui/icons-material";
import styles from './styles/Login.module.css'; // Importing the modular CSS

const Login = () => {
  const navigate = useNavigate();

  const [showPassword, setShowPassword] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleClickShowPassword = () => setShowPassword(!showPassword);

  const handleMouseDownPassword = (event) => {
    event.preventDefault();
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const loginResponse = await axios.post('/api/user/signIn', {email, password});
    if(loginResponse.data.status){
      // login successful
      navigate('/');
    }else{
      // login failed

    }
  };

  return (
    <div className={styles.loginPage}>
      <div className={styles.loginContainer}>
        <Typography variant="h4" className={styles.title}>
          Welcome Back
        </Typography>
        <form onSubmit={handleSubmit} className={styles.loginForm}>
          <TextField
            label="Email"
            type="email"
            variant="outlined"
            fullWidth
            margin="normal"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className={styles.inputField}
            required
          />
          <TextField
            label="Password"
            type={showPassword ? "text" : "password"}
            variant="outlined"
            fullWidth
            margin="normal"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className={styles.inputField}
            required
            InputProps={{
              endAdornment: (
                <InputAdornment position="end">
                  <IconButton
                    onClick={handleClickShowPassword}
                    onMouseDown={handleMouseDownPassword}
                    edge="end"
                  >
                    {showPassword ? <Visibility /> : <VisibilityOff />}
                  </IconButton>
                </InputAdornment>
              ),
            }}
          />
          <Button
            type="submit"
            variant="contained"
            color="primary"
            fullWidth
            className={styles.submitButton}
          >
            Login
          </Button>
          <Typography variant="body2" className={styles.signupLink}>
            Donâ€™t have an account?{" "}
            <a href="/signup" className={styles.signupAnchor}>
              Sign Up
            </a>
          </Typography>
        </form>
      </div>
    </div>
  );
};

export default Login;
